files.css:
#contenedor {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background-color: #f8c1da; /* Color de fondo inicial (rosado) */
    transition: background-color 0.5s; /* Agregar transición para el cambio de color */
}

#miParrafo {
    cursor: pointer;
}

files.js:
document.addEventListener("DOMContentLoaded", function() {
    // Obtener el elemento del párrafo
    const miParrafo = document.getElementById("miParrafo");

    // Agregar un evento de clic para cambiar el color
    miParrafo.addEventListener("click", function() {
        // Cambiar el color de fondo a azul claro
        document.getElementById("contenedor").style.backgroundColor = "#a1c9f7";
    });
});

